    var array: Array<CharArray> = input.split("\n").map { i -> i.toCharArray() }.toTypedArray()
    var total: Long = 0;
    var changed = 1;
    while (changed > 0) {
        changed = 0
        for (i in 0..array.size - 1) {
            for (j in 0..array[0].size - 1) {
                var count = 0
                for (k in max(0, i - 1)..min(array.size - 1, i + 1)) {
                    for (n in max(0, j - 1)..min(array[0].size - 1, j + 1)) {
                        if (k == i && n == j) continue
                        if (array[k][n] != '.') {
                            count++
                        }
                    }
                }
                if (count < 4 && array[i][j] == '@') {
                    array[i][j] = 'x'
                    total++
                }

            }
        }
        for (i in 0..array.size - 1) {
            for (j in 0..array[0].size - 1) {
                if (array[i][j] == 'x') {
                    array[i][j] = '.'
                    changed++
                }
            }
        }
    }


    for (i in 0..array.size - 1) {
        println(array[i])
    }
    println(total)
